* {
  margin: 0;
  padding: 0;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
}

.main-layout {
  background-image: url("9f6d10202f94d402ea675c5510c016e6.jpg");
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-size: cover;
  position: relative;
}

.main-layout .content {
  z-index: 2;
  text-align: center;
}

.main-layout::after {
  background-color: rgba(0, 0, 0, 0.6);
  z-index: 1;
  content: "";
  width: 100%;
  height: 100%;
  position: absolute;
}

.content h2 {
  color: white;
  animation: textAnimate 2s infinite forwards ease-in-out alternate-reverse;
  font-weight: 100;
  margin-bottom: 15px;
}


.content a {
  color: white;
  text-decoration: none;
  padding: 10px 20px;
  font-size: 1.5em;
  border: 3px solid white;
  background: transparent;
  border-radius: 15px;
  transition: 0.5s;
}

a.go-back {
  padding: 5px 15px;
  text-decoration: none;
  font-size: 2em;
  color: grey;
  margin-bottom: 20px;
  display: block;
}

.content a:hover {
  color: black;
  background-color: white;
  transition: 0.5s;
}

.duty-content {
  padding: 20px;
  text-align: center;
  width: 85%;
  margin: auto;
}

.duty-content input {
  font-size: 1.5em;
  padding: 5px;
  outline: none;
  border: 2px solid grey;
  border-radius: 15px;
  width: 100%;
}

tr:hover {
  background-color: #f5f5f5;
}

tr.hide {
  display: none;
}

th {
  background-color: rgb(76, 111, 175);
  color: white;
}

tr:nth-child(even) {
  background-color: #c5c5c5;
}

.day-and-date {
  font-size: 2.3em;
  margin: 15px 0;
}

.table-wrapper {
  width: 100%;
  overflow: auto;
}

.table-wrapper table {
  border-collapse: collapse;
  width: 100%;
}

table,
th,
td {
  border: 1px solid black;
  text-align: left;
}

th,
td {
  padding: 5px 15px;
}

@keyframes textAnimate {
  from {
    font-size: 3em;
  }

  to {
    font-size: 3.5em;
  }
}
